import { Button } from '@material-ui/core';
import React,{useEffect} from 'react';
import history from '../History';
import '../css/Userprofile.css';
import {connect} from 'react-redux';
import {userprofileAction,userAction} from '../actions';
import {db} from '../firebaseConfig';

const Userprofile=(props)=>{

    useEffect(()=>{
        console.log(props.users.user.uid)
        db.collection('users').doc(props.users.user.uid).onSnapshot(snapshot=>{
        //    console.log(snapshot.data())
             props.userprofileAction(snapshot.data());
                   })

            },[props.users.user.uid])




    return(<div>
       <Button  className="back_btn" style={{marginTop:'-45px'}} type="submit" onClick={()=>history.push("/")}>Back</Button>
       <div className="user_info">
           {console.log(props.users.userprofile)}
       {props.users.userprofile && props.users.userprofile.map(({id, userdetail})=>{ 
                  return <div key={id}><strong>{userdetail.bio}</strong> : {userdetail.followers} : {userdetail.following}</div>
                     }) }
       </div>
        </div>);
}

const mapStateToProps=(state)=>{
    return {users:state}
}

export default connect(mapStateToProps,{userprofileAction,userAction}) (Userprofile);
