import React, { useEffect } from 'react';
import '../css/Post.css';
import Avatar from '@material-ui/core/Avatar';
import {commentAction,singlecommentAction} from '../actions';
import {connect} from 'react-redux';
import {db} from '../firebaseConfig';

function Post(props) {

    useEffect(()=>{

        let unsubscribe;
        if(props.postId)
        {
            //  unsubscribe=db.collection('posts').doc(`${props.postId}`).collection('comments').get()
            //  .then(querySnapshot => {
            //     querySnapshot.forEach(doc =>{
            //         console.log("data"+doc.data());
            //         props.commentAction(doc.data())})})
               unsubscribe=db.collection('posts').doc(props.postId).collection('comments').onSnapshot(snapshot=>{
                        props.commentAction(snapshot.docs.map(doc=>({
                            id:doc.id,
                            comment:doc.data()
                        })));
                    })
                
                }
        // return  ()=>{
        //     unsubscribe();
        // }
    },[props.postId]);

    const postComment=(event)=>{

    }
    return (
        <div className="post">
            <div className="post_header">
            <Avatar  className="post_avatar" alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
            <p>{props.username}</p> 
             </div>
            
         <img className="post_image" src={props.imageUrl} alt="b" />  
         <h4 className="post_text"><p ><strong>{props.username} : </strong>{props.caption}</p></h4>
 
                 <div className="post_comments">
                     {console.log(typeof props.comments.comment)}
                     {/* {  props.comments.comment?(
                         props.comments.comment.map((comm)=>(
                            <p key={comm}>
                            {comm.username}: {comm.text}
                            </p>
                         ))):null
                        } */}
                 </div>
             
             <form className="post_comment">
                <input
                 className="post_input"
                 type="text"
                 placeholder="Add a comment"
                 value={props.comments.singlecomment}
                 onChange={(e)=>props.singlecommentAction(e.target.value)}>
                </input>
                <button
                className="post_button"
                disabled={!props.singlecomment}
                type="submit"
                onClick={postComment}>
                        Post
                </button>
             </form>
             </div>      
      
    )
}
const mapStateToProps=state=>{
    return {
        comments:state
    }
};

export default connect(mapStateToProps,{commentAction,singlecommentAction})(Post);
