const initialState={
    open:false,
    email:'',
    username:'',
    password:'',
    user:null,
    openSignIn:false
};

export default ModalRed=(state=initialState,action)=>{

    switch(action.type){

        case 'usernameac': 
            return  {...this.state,username:action.payload }  
        case 'emailac': 
            return {...this.state,email:action.payload }  
        case 'passwordac': 
            return  {...this.state,password:action.payload }  
        default:state
  }

}
