import { Button } from '@material-ui/core';
import React from 'react';
import {connect} from 'react-redux';
import {postAction,progressAction,imageAction} from '../actions';
import {db,storage} from '../firebaseConfig';

const ImageUpload=(props)=>{

    const handleChange=e=>{
        if(e.target.files[0]){
            props.imageAction(e.target.files[0]);
        }
    };

    const handleUpload=()=>{

        const uploadTask=storage.ref(`images/${props.posts.image.name}`).put(props.posts.image);

        uploadTask.on('state_changed',
        (snapshot)=>{
            //progress bar logic
            const progress=Math.round((snapshot.bytesTransferred/snapshot.totalBytes)*100);
            props.progressAction(progress);
        },
        (error)=>{alert.message(error);
        },
        ()=>{
                //on update
                storage
                    .ref("/images")
                    .child(props.posts.image.name)
                    .getDownloadURL()
                    .then(url=>
                        db.collection("posts").add({timestamp:firebase.firestore.FieldValue.serverTimestamp(),
                        caption:props.posts.caption,
                        imageUrl:url,
                        username:props.username
                    }))


        }
        )
    };
    return(
    <div>
        <input type="text" placeholder="Enter a caption" value={props.posts.caption} onChange={event=>props.postAction(event.target.value)}/>
        <input type="file" onChange={handleChange} />
        <Button onClick={handleUpload} > Upload </Button>

    </div>
    );
};

const mapStateToProps=(state)=>{
    return { posts:state}
}

export default connect(mapStateToProps,{postAction,progressAction,imageAction}) (ImageUpload);
