import React, { useEffect } from 'react';
import '../css/Post.css';
import Avatar from '@material-ui/core/Avatar';
import {commentAction,singlecommentAction} from '../actions';
import {connect} from 'react-redux';
import {db} from '../firebaseConfig';

function Post(props) {

    useEffect(()=>{

        let unsubscribe;
        if(props.postId)
        {
            unsubscribe=db
                        .collection("posts")
                        .doc(props.postId)
                        .collection("comments")
                        .onSnapshot((snapshot)=>{
                            props.commentAction(snapshot.docs.map((doc)=>doc.data()));
                        });
        }

        return  ()=>{
            unsubscribe();
        }
    },[props.postId]);

    const postComment=(event)=>{

    }
    return (
        <div className="post">
            <div className="post_header">
            <Avatar  className="post_avatar" alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
            <p>{props.username}</p> 
             </div>
             {console.log(props.comments.comment)}
        
         <img className="post_image" src={props.imageUrl} alt="b" />  
         <div className="post_text">
             <p ><strong>{props.username} : </strong>{props.caption}</p>   
             
                 <div className="post_comments">
                    
                     {  
                         props.comments.comment.map((comment)=>(
                            <p key={comment}>
                            <b>{comment.username}: </b>{comment.text}
                            </p>
                         ))
                        }
                 </div>
             
             <form className="post_comment">
                <input
                 className="post_input"
                 type="text"
                 placeholder="Add a comment"
                 value={props.comments.singlecomment}
                 onChange={(e)=>props.singlecommentAction(e.target.value)}>
                </input>
                <button
                className="post_button"
                disabled={!props.singlecomment}
                type="submit"
                onClick={postComment}>
                        Post
                </button>
             </form>
             </div>      
        </div>
    )
}
const mapStateToProps=state=>{
    return {
        comments:state
    }
};

export default connect(mapStateToProps,{commentAction,singlecommentAction})(Post);
