import React,{useState,useEffect} from 'react';
import Modal from '@material-ui/core/Modal';
import '../css/App.css';
import {auth} from '../firebaseConfig';
import ModalBody from './ModalBody';
import { Button, Input } from '@material-ui/core';
import {emailAction,usernameAction,passwordAction,openAction,openSignInAction,userAction} from '../actions';
import {connect} from 'react-redux';

const SimpleModal=(props)=> {

  // const [open, setOpen] = React.useState(false);
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = React.useState('');
  // const [username,setUsername] = React.useState('');
  // const [user,setUser]=React.useState(null);
  // const [openSignIn,setOpenSignIn]=useState(false);

  useEffect(()=>{
   const unsubcrcibe= auth.onAuthStateChanged(authUser=>{
      if(authUser){
        console.log(authUser);
        props.posts.userAction(authUser);

      }
      else{
        props.posts.userAction(null);
      }
    });
    return ()=>{
      unsubcrcibe();
    };
  },[props.posts.user,props.posts.username]);

  const signUp=event=>{
    event.preventDefault();
    auth.createUserWithEmailAndPassword(props.posts.email,props.posts.password)
    .then(authUser=>{
      return authUser.props.posts.user.updateProfile({displayName:props.posts.username});
    })
    .catch((error)=>alert(error.message));
    // setOpen(false);
  };

  const signIn=event=>{
    event.preventDefault();
    auth.signInWithEmailAndPassword(props.posts.email,props.posts.password)
    .catch((error)=>alert(error.message));
    // setOpenSignIn(false);
  };

  const usernameSet=(obj)=>{
    props.usernameAction(obj);
  };
  const emailSet=(obj)=>{
    props.emailAction(obj);
  };
  const passwordSet=(obj)=>{
    props.passwordAction(obj);
  };
  const logoutSet=()=>{
    auth.signOut();
    props.posts.openAction(false);
    props.posts.openSignInAction(false);
  }


       
    const body =props.posts.open?
      <ModalBody type="signup" text="Sign up" click={signUp} email={props.posts.email} password={props.posts.password} username={props.posts.username} setusername={usernameSet} setPassword={passwordSet}  setEmail={emailSet} />
    :<ModalBody type="signin" text="Sign In" click={signIn} email={props.posts.email} password={props.posts.password} username={props.posts.username} setusername={usernameSet} setPassword={passwordSet}  setEmail={emailSet}   />

    const body1=
     props.posts.open?
    <Modal
      open={props.posts.open} 
      onClose={()=>props.posts.openAction(false)} 
      >
       {body}
    </Modal>: 
    <Modal
      open={props.posts.openSignIn}
      onClose={()=>props.posts.opensigninAction(false)}  >
       {body} 
    </Modal> 
    

  return (
    <div>
      {props.posts.user?
    <Button onClick={logoutSet}>Log out</Button>
  :(<div className="app_loginContainer">
     <Button onClick={()=>props.posts.openisigninAction(true)}>Sign In</Button>
    <Button onClick={()=>props.posts.openAction(true)}>Sign up</Button>
    {body1}
    </div>)
      }
    </div>
  );
    };

    const mapStatetoProps=(state)=>{
      return { posts:state }
    };


    export default connect(mapStatetoProps,{emailAction,usernameAction,passwordAction,openAction,openSignInAction,userAction}) (SimpleModal);
